from PyQt5.QtGui import QStandardItemModelfrom PyQt5.QtWidgets import QApplication, QWidget, QScrollArea, QTableWidget, \    QVBoxLayout, QTableWidgetItem, QTableViewimport pandas as pdimport sysdef ui(prices):    app = QApplication(sys.argv)    win = QWidget()    scroll = QScrollArea()    layout = QVBoxLayout()    table = QTableWidget()    data = pd.DataFrame(prices)    table.setColumnCount(len(data.columns))    table.setRowCount(len(data.index))    for i in range(len(data.index)):        for j in range(len(data.columns)):            table.setItem(i, j, QTableWidgetItem(str(data.iloc[i, j])))    table.setHorizontalHeaderLabels(list(data.columns))    table.setVerticalHeaderLabels(list(data.index))    scroll.setWidget(table)    layout.addWidget(table)    win.setLayout(layout)    win.show()    sys.exit(app.exec_())